mapper.select:
SELECT 

FROM ter_merchant a

WHERE a.id = #{id}

SELECT 

FROM ter_merchant a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.merchant_num = #{merchantNum}


		AND a.merchant_name LIKE 
			'%'||#{merchantName}||'%'
			'%'+#{merchantName}+'%'
			concat('%',#{merchantName},'%')


		AND a.telphone = #{telphone}


		AND a.salesman = #{salesman}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ter_merchant a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ter_merchant SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
